mapper.update:
update community
set b_view_count = b_view_count+1
where b_num = #{b_num}

update cmu_reply
set re_contents = #{re_contents}
where re_no = #{re_no}

update COMMUNITY
set b_state = -1
where b_num = #{b_num}

update CMU_REPLY
set re_state = -1
where b_num =  #{b_num}

update Community
set b_title = #{b_title},b_contents= #{b_contents},b_image= #{b_image}
where b_num = #{b_num}

update Community
set b_like_count = b_like_count+1
where b_num = #{b_num}

mapper.select:
select b_like_count
from Community
where b_num = #{b_num}

mapper.update:
update Cmu_reply
set RE_STATE = -1
where re_no = #{re_no}

update Cmu_reply
set re_contents = #{re_contents}, re_date = sysdate
where re_no = #{re_no}
